+        --newtab-card-background-color: color-mix( in srgb, var(--newtab-background-color) 97%, var(--newtab-wordmark-color)) !important;
+        /*fondo de actividad reciente*/
+        --newtab-text-secondary-color: var( --newtab-wordmark-color) !important;
+        /*Label secundaria*/
+        --newtab-text-primary-color: var( --newtab-wordmark-color) !important;
+        /*label general*/
+        --newtab-overlay-color: #10101010 !important;
+        /*fondo de editar, transparente*/
+        --newtab-textbox-border: var( --newtab-overlay-color) !important;
+        /*contorno de txt al editar un sitio*/
+        --customize-menu-seperator-line-color: color-mix( in srgb, var(--newtab-wordmark-color), var(--newtab-background-color) 80%) !important;
+        /*Linea separadora en "personalizar"*/
+        --newtab-primary-action-background: #304af1 !important;
+        /*color de fondo de Boton "Guardar" y fondo de radiobuttons */
+        /*--newtab-button-secondary-color: #f02020c0 !important;                            color del botón "cancelar"*/
