/* Source file made available under Mozilla Public License v. 2.0 See the main repository for updates as well as full license text. 
   https://github.com/Godiesc/AlpenBlue */

:root {
  scrollbar-color: #38375580 #18173530 !important; /*Color de la barra de desplazamiento*/
  scrollbar-width: thin !important;
}

window,
dialog {
  /*Colores varios de paginas de about:addons y about:settings*/
  background-color: #1d1d3bfa !important;
  --in-content-primary-button-background: #3080dc !important;
  --in-content-primary-button-text-color: #192045 !important;
  --in-content-primary-button-background-hover: #1080ff !important; /*color de los radiobutton (fondo) :hover*/
  --in-content-primary-button-background-active: #3399f0 !important; /*color de los radiobutton (clic) con check*/

  --checkbox-unchecked-bgcolor: #46466f !important; /*color de los checkbox vacíos*/
  --checkbox-unchecked-hover-bgcolor: #4050ee !important; /*color de los checkbox hover*/
  --checkbox-unchecked-active-bgcolor: #26264f !important; /*color de los checkbox al darle clic*/
  --checkbox-checked-bgcolor: #1040bc !important; /*color de los checkbox con check*/
  --checkbox-checked-color: #fff !important; /*color de la palomita check*/
  --checkbox-checked-hover-bgcolor: #cc3535 !important; /*color de los checkbox con check hover*/

  --in-content-box-background: #1d1d38 !important;
  --in-content-box-border-color: #3d3d88 !important;

  --in-content-button-background: #2b2a4f !important; /*color de botones*/
  --in-content-button-background-hover: #2d2db0 !important; /*color de botones :hover*/
  --in-content-button-background-active: #2d2db0 !important; /*color de botones al darle clic*/
}
:host,
:root {
  /*Colores varios de paginas de about:addons y about:settings*/
  --in-content-page-background: #1c1b32 !important; /*Fondo general*/
  --in-content-text-color: #fff !important; /*Color general de label*/
  --in-content-page-color: #fff !important; /*color de texto general*/
  --checkbox-unchecked-bgcolor: #36364f !important; /*color de los checkbox vacíos*/
  --checkbox-unchecked-hover-bgcolor: #4050ee !important; /*color de los checkbox hover*/
  --checkbox-unchecked-active-bgcolor: #26264f !important; /*color de los checkbox al darle clic*/

  --checkbox-checked-bgcolor: #1040bc !important; /*color de los checkbox con check*/
  --checkbox-checked-color: #fff !important; /*color de la palomita check*/
  --checkbox-checked-hover-bgcolor: #cc3535 !important; /*color de los checkbox con check hover*/

  --in-content-primary-button-text-color: #192045 !important; /*color de los radiobutton (circulo) con check*/
  --in-content-primary-button-background-hover: #1080ff !important; /*color de los radiobutton (fondo) :hover*/
  --in-content-primary-button-background-active: #3399f0 !important; /*color de los radiobutton (clic) con check*/

  --in-content-box-background: #2b2a5f !important; /*color de los textbox*/
  --in-content-box-border-color: #6060f0 !important; /*color del borde de los textbox*/
  --in-content-box-background-odd: #35355f !important; /*color destinción entre filas de una tabla-atajos de busqueda*/
  --in-content-box-info-background: #30306f !important; /*color de la caja de elementos que se sincronizan*/

  --in-content-primary-button-background: #1050ff !important; /*color de la label colorida y de enlaces*/
  --in-content-deemphasized-text: #fff !important; /*color de la descripción  de los temas y extensiones*/

  --in-content-border-color: #253690 !important; /*Linea separadora en la configuración*/

  --in-content-button-background: #2b2a4f !important; /*color de botones*/
  --in-content-button-background-hover: #2d2db0 !important; /*color de botones :hover*/
  --in-content-button-background-active: #2d2db0 !important; /*color de botones al darle clic*/

  --in-content-link-color: #0099ff !important; /*color de links*/
  --in-content-link-color-hover: #00ccff !important; /*color de links*/
  --in-content-link-color-active: #0099ff !important; /*color de links*/
  --in-content-link-color-visited: #0099ff !important; /*color de links*/

  --in-content-accent-color: #4040fb !important; /*color de label del menú izquiero seleccionado*/
  --in-content-accent-color-active: #5089ff !important; /*color de label del menú izquiero seleccionado (al dar clic)*/
  --card-outline-color: #2d2de0 !important; /*Color de borde al pasar por elementos de addons*/

  --in-content-table-background: #131424 !important; /*Color de fondo de tablas de "about:support"*/

  --in-content-item-hover: #2d4db0 !important; /*Color de fondo al pasar por los items de un combobox"*/
  --in-content-item-hover-text: #ffffff !important; /*Color de label del item hover del combobox"*/
  --in-content-item-selected: #3d4df0 !important; /*Color de fondo al pasar por los items de un combobox (teclas de direccion)"*/
  --in-content-item-selected-text: #ffffff !important; /*Color de label al pasar por los items de un combobox (teclas de direccion)"*/

  --in-content-focus-outline-color: #3089ff !important; /*color de focus de txt "buscar..."*/
